//   firstName: ''
// }

const ExpReport = ({ stepper, infoExp, changeInfo }) => {
  // ** Hooks
//   const dispatch = useDispatch()
    console.log('infoExp:', infoExp)
    
    const defaultValues = {
        lastName: '',
        firstName: ''
    }
    const {
        handleSubmit
      } = useForm({ defaultValues })
    const onSubmit = () => {
        // if (Object.values(data).every(field => field.length > 0)) {
        stepper.next()
    
    }
    const [infoModel, setModel] = useState({
        id: 1,
        datasetdescription: '',
        datasetfolderurl: '',
        datasetname: '',
        datasetowner: 1,
        datasetsoftID: 1,
        datasetsum: 1,
        datasettype: 1,
      })
    return (
    <Fragment>
       <div>
        <Row>
          <Col className='p-0' xl='8'>
            <div className='content-header'>
              <h5 className='mb-0'>Tổng quan bài thí nghiệm</h5>
            </div>
          </Col>
        </Row>
      </div>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Row>
          <Col md='8' className='mb-1'>
            <Label className='form-label' for='city'>
              Tên bài thí nghiệm
            </Label>
          </Col>
        </Row>
        <Row>
         
        </Row>
        <Row>
         
        </Row>
        <Row>
         
        </Row>
        <div className='d-flex justify-content-end'>
          <Button type='submit' color='primary' className='btn-next'>
            <span className='align-middle d-sm-inline-block d-none'>Tiếp theo</span>
            <ArrowRight size={14} className='align-middle ms-sm-25 ms-0'></ArrowRight>
          </Button>
        </div>
      </Form>
      
    </Fragment>
  )
}

export default ExpReport
