import { useState, Fragment } from 'react'

// ** Reactstrap Imports
import { Card, CardHeader, CardTitle, CardBody, Button, ListGroup, ListGroupItem, Row, Col, Form } from 'reactstrap'
import { useForm, Controller } from 'react-hook-form'

// ** Third Party Imports
import { useDropzone } from 'react-dropzone'
import { FileText, X, DownloadCloud, ArrowRight, ArrowLeft } from 'react-feather'
import axios from 'axios'

const Predict = ({ stepper, infoExp, info }) => {
  const {
    control,
    setError,
    handleSubmit,
    formState: { errors }
  } = useForm({})
  // ** State
  const [files, setFiles] = useState([])
  const [files1, setFiles1] = useState([])
  const [display, setDisplay] = useState('block')
  const [display1, setDisplay1] = useState('block')
  const [displayOutput, setDisplayOutput] = useState(1)

  const [url, setUrl] = useState()
  const { getRootProps, getInputProps } = useDropzone({
    multiple: false,
    onDrop: acceptedFiles => {
      setFiles([...files, ...acceptedFiles.map(file => Object.assign(file))])
    }
  })
  const DowLoadReport = () => {
    const url = process.env.REACT_APP_API_URL
    axios({
      url: `${url}/download-report/?configID=96`,
      method: 'GET',
      responseType: 'blob', // important
    }).then((response) => {
      const url = window.URL.createObjectURL(new Blob([response.data]))
      const link = document.createElement('a')
      link.href = url
      link.setAttribute('download', 'report.docx')
      document.body.appendChild(link)
      link.click()
    })

  }
  const renderFilePreview = file => {
    if (file.type.startsWith('image')) {
      return (
        <Row className='my-2'>
          <Col className='d-flex align-items-center justify-content-center mb-2 mb-md-0' md='6' xs='6'>
            <div className='d-flex align-items-center justify-content-center'>
              <img className='img-fluid product-img' src={URL.createObjectURL(file)} alt={'áº£nh test'} />
            </div>
          </Col>
        </Row>
      )
    } else {
      return <FileText size='28' />
    }
  }

  const fileList = files.map((file, index) => (
    <ListGroupItem key={`${file.name}-${index}`} className='d-flex align-items-center justify-content-between'>
      <div className='file-details d-flex align-items-center'>
        <div className='file-preview me-1'>{renderFilePreview(file)}</div>
      </div>
    </ListGroupItem>
  ))
  const onSubmit = data => {
    const url = process.env.REACT_APP_API_URL
    axios.get(`${url}/experiment/get_predict_result/?id_predict=6`
      , {
        headers: {
          'Content-Type': 'application/json',
          // Authorization: `Bearer ${localStorage.getItem("accessToken")}`
        },

      }).then(response => {

        const temp = response.data.data.data.outputpath

        if (temp === null) {
          setDisplayOutput(2)
        } else {
          setDisplayOutput(3)

          const path = url + temp?.slice(1, temp.length) + response.data.data.result[0]
          setUrl(path)
        }
      })

  }
  const handleRemoveAllFiles = () => {
    setFiles([])
    setDisplay('block')
    setDisplay1('block')
  }
  if (infoExp.expsoftwarelibid === 1) {
    return (
      <Card>
        <CardHeader>
        </CardHeader>
        <CardBody>
          <div {...getRootProps({ className: 'dropzone' })} style={{ display: display }}>
            <input {...getInputProps()} />
            <div className='d-flex align-items-center justify-content-center flex-column'>
              <DownloadCloud size={64} />
              <h5>Chá»n áº£nh khuÃ´n máº·t</h5>
              <p className='text-secondary'>
                Drop files here or click{' '}
                <a href='/' onClick={e => {
                  e.preventDefault()
                  setDisplay('none')
                }}>
                  browse
                </a>{' '}
                thorough your machine
              </p>
            </div>
          </div>
          <div {...getRootProps({ className: 'dropzone' })} style={{ display: display1 }}>
            <input {...getInputProps()} />
            <div className='d-flex align-items-center justify-content-center flex-column'>
              <DownloadCloud size={64} />
              <h5>Chá»n áº£nh phÃ¡t hiá»n khuÃ´n máº·t</h5>
              <p className='text-secondary'>
                Drop files here or click{' '}
                <a href='/' onClick={e => {
                  e.preventDefault()
                  setDisplay1('none')
                }}>
                  browse
                </a>{' '}
                thorough your machine
              </p>
            </div>
          </div>
          {files.length ? (
            <Fragment>
              <ListGroup className='my-2'>{fileList}</ListGroup>
              {
                url &&
                <Row>
                  <Col className='d-flex align-items-center justify-content-center mb-2 mb-md-0' md='6' xs='12'>
                    <div className='d-flex align-items-center justify-content-center'>
                      {
                        displayOutput === 1 ? <></> : displayOutput === 2 ? <p>KhÃ´ng cÃ³ káº¿t quáº£</p> : <img className='img-fluid product-img' src={url} alt={'áº£nh Äáº§u ra'} />
                      }
                    </div>
                  </Col>
                </Row>
              }

              <div className='d-flex justify-content-end'>
                <Form onSubmit={handleSubmit(onSubmit)}>
                  <Button className='me-1' color='danger' outline onClick={handleRemoveAllFiles}>
                    Remove All
                  </Button>
                  <Button type='submit' color='primary'>Kiá»m tra</Button>
                </Form>
              </div>
            </Fragment>
          ) : null}
          <div className='d-flex justify-content-between' style={{ marginTop: '20px' }}>
            <Button type='button' color='primary' className='btn-prev' onClick={() => stepper.previous()}>
              <ArrowLeft size={14} className='align-middle me-sm-25 me-0'></ArrowLeft>
              <span className='align-middle d-sm-inline-block d-none'>Previous</span>
            </Button>
            <Button type='button' color='primary' className='btn-next' onClick={() => DowLoadReport()}>
              <span className='align-middle d-sm-inline-block d-none'>Xuáº¥t bÃ¡o cÃ¡o</span>
            </Button>
          </div>
        </CardBody>
      </Card >
    )
  } else if (infoExp.expsoftwarelibid === 2) {
    return (
      <Card>
        <CardHeader>
        </CardHeader>
        <CardBody>
          <div {...getRootProps({ className: 'dropzone' })} style={{ display: display }}>
            <input {...getInputProps()} />
            <div className='d-flex align-items-center justify-content-center flex-column'>
              <DownloadCloud size={64} />
              <h5>Chá»n video phÃ¡t hiá»n hÃ nh Äá»ng</h5>
              <p className='text-secondary'>
                Drop files here or click{' '}
                <a href='/' onClick={e => e.preventDefault()}>
                  browse
                </a>{' '}
                thorough your machine
              </p>
            </div>
          </div>
          {files.length ? (
            <Fragment>
              <ListGroup className='my-2'>{fileList}</ListGroup>
              {
                url &&
                <Row>
                  <Col className='d-flex align-items-center justify-content-center mb-2 mb-md-0' md='6' xs='12'>
                    <div className='d-flex align-items-center justify-content-center'>
                      {
                        displayOutput === 1 ? <></> : displayOutput === 2 ? <p>KhÃ´ng cÃ³ káº¿t quáº£</p> : <img className='img-fluid product-img' src={url} alt={'áº£nh Äáº§u ra'} />
                      }
                    </div>
                  </Col>
                </Row>
              }
              <div className='d-flex justify-content-end'>
                <Form onSubmit={handleSubmit(onSubmit)}>
                  <Button className='me-1' color='danger' outline onClick={handleRemoveAllFiles}>
                    Remove All
                  </Button>
                  <Button type='submit' color='primary'>Kiá»m tra</Button>
                </Form>
              </div>
            </Fragment>
          ) : null}
          <div className='d-flex justify-content-between' style={{ marginTop: '20px' }}>
            <Button type='button' color='primary' className='btn-prev' onClick={() => stepper.previous()}>
              <ArrowLeft size={14} className='align-middle me-sm-25 me-0'></ArrowLeft>
              <span className='align-middle d-sm-inline-block d-none'>Previous</span>
            </Button>
            <Button type='button' color='primary' className='btn-next' onClick={() => DowLoadReport()}>
              <span className='align-middle d-sm-inline-block d-none'>Xuáº¥t bÃ¡o cÃ¡o</span>
            </Button>
          </div>
        </CardBody>
      </Card>
    )
  } else {
    return (
      <Card>
        <CardHeader>
        </CardHeader>
        <CardBody>
          <div {...getRootProps({ className: 'dropzone' })} style={{ display: display }}>
            <input {...getInputProps()} />
            <div className='d-flex align-items-center justify-content-center flex-column'>
              <DownloadCloud size={64} />
              <h5>Chá»n áº£nh phÃ¡t hiá»n khuÃ´n máº·t</h5>
              <p className='text-secondary'>
                Drop files here or click{' '}
                <a href='/' onClick={e => e.preventDefault()}>
                  browse
                </a>{' '}
                thorough your machine
              </p>
            </div>
          </div>
          {files.length ? (
            <Fragment>
              <ListGroup className='my-2'>{fileList}</ListGroup>
              {
                url &&
                <Row>
                  <Col className='d-flex align-items-center justify-content-center mb-2 mb-md-0' md='6' xs='12'>
                    <div className='d-flex align-items-center justify-content-center'>
                      
                      <img className='img-fluid product-img' src={url} alt={'áº£nh Äáº§u ra'} />
                    </div>
                  </Col>
                </Row>
              }
              <div className='d-flex justify-content-end'>
                <Form onSubmit={handleSubmit(onSubmit)}>
                  <Button className='me-1' color='danger' outline onClick={handleRemoveAllFiles}>
                    Remove All
                  </Button>
                  <Button type='submit' color='primary'>Kiá»m tra</Button>
                </Form>
              </div>
            </Fragment>
          ) : null}
          <div className='d-flex justify-content-between' style={{ marginTop: '20px' }}>
            <Button type='button' color='primary' className='btn-prev' onClick={() => stepper.previous()}>
              <ArrowLeft size={14} className='align-middle me-sm-25 me-0'></ArrowLeft>
              <span className='align-middle d-sm-inline-block d-none'>Previous</span>
            </Button>
            <Button type='button' color='primary' className='btn-next' onClick={() => DowLoadReport()}>
              <span className='align-middle d-sm-inline-block d-none'>Xuáº¥t bÃ¡o cÃ¡o</span>
            </Button>
          </div>
        </CardBody>
      </Card>
    )
  }

}

