// ** React Imports
import { Fragment, useState, useEffect } from 'react'

// ** Third Party Components
import Select from 'react-select'
import { useForm, Controller } from 'react-hook-form'
import { ArrowLeft, ArrowRight } from 'react-feather'

// ** Utils
import { selectThemeColors } from '@utils'
import { useSelector, useDispatch } from 'react-redux'
// ** Reactstrap Imports
import { Label, Row, Col, Button, Form, Input, FormFeedback, CardText, Modal, ModalBody, ModalHeader } from 'reactstrap'
import FileUploaderSingle from './FileUploaderSingle'
import { getListModelBySoftID } from '@store/action/model'
// ** Styles
import '@styles/react/libs/react-select/_react-select.scss'

// const defaultValues = {
//   lastName: '',
//   firstName: ''
// }

const ExpReport = ({ stepper, infoExp, changeInfo }) => {
  // ** Hooks
//   const dispatch = useDispatch()
    console.log('infoExp:', infoExp)
    const selectThemeColors = (theme) => ({
        ...theme,
        colors: {
          ...theme.colors,
          primary25: '#dae1e7', // màu khi hover
          primary: '#1b4d89',   // màu chủ đạo
        },
      });
    const defaultValues = {
        lastName: '',
        firstName: ''
    }
    const languageOptions = [
        { value: 1, label: 'Nhận diện khuôn mặt' },
        { value: 2, label: 'Nhận dạng sự kiện bất thường' },
        { value: 3, label: 'Phát hiện khuôn mặt' },
    ]
    const dataDataset = useSelector((state) => {
        return state.dataset.dataDataset
    })
    const ChangeLisData = (dataDataset) => {
        const list = []
        dataDataset.map(item => {
          list.push({
            value: JSON.stringify({
              id: item.datasetid,
              datasetdescription: item.datasetdescription,
              datasetfolderurl: item.datasetfolderurl,
              datasetname: item.datasetname,
              datasetowner: item.datasetowner,
              datasetsoftID: item.datasetsoftID,
              datasetsum: item.datasetsum,
              datasettype: item.datasettype,
            }),
            label: item.datasetname
          })
        })
        return list
      }
    //   const setData = (dataDataset) => {
    //     // console.log(dataDataset)
    //     // console.log(infoExp.expdatasetid)
    //     if (dataDataset.length !== 0) {
    //       const data = dataDataset.find(item => item.datasetid === infoExp.expdatasetid)
    //       console.log(dataDataset)
    //       const temp = {
    //         value: data.datasetid,
    //         label: data.datasetname
    //       }
    //       return temp
    //     }
    
    //   }
    const {
        handleSubmit
      } = useForm({ defaultValues })
    const onSubmit = () => {
        // if (Object.values(data).every(field => field.length > 0)) {
        stepper.next()
    
    }
    const [infoModel, setModel] = useState({
        id: 1,
        datasetdescription: '',
        datasetfolderurl: '',
        datasetname: '',
        datasetowner: 1,
        datasetsoftID: 1,
        datasetsum: 1,
        datasettype: 1,
      })
    return (
    <Fragment>
       <div>
        <Row>
          <Col className='p-0' xl='8'>
            <div className='content-header'>
              <h5 className='mb-0'>Tổng quan bài thí nghiệm</h5>
            </div>
          </Col>
        </Row>
      </div>
      <Form onSubmit={handleSubmit(onSubmit)}>
        {/* <Row>
          <Col md='8' className='mb-1'>
            <Label className='form-label' for='city'>
              Tên bài thí nghiệm
            </Label>
          </Col>
        </Row> */}
        <Row>
        <Col md='8' className='mb-1'>
            <Select
              isMulti={false}
              isClearable={false}
              placeholder={'Chọn bài toán'}
              theme={selectThemeColors}
              id={`language`}
              options={languageOptions}
              value={{ value: infoExp.expsoftwarelibid, label: infoExp.expsoftwarelibid === 1 ? 'Nhận diện khuôn mặt' : infoExp.expsoftwarelibid === 2 ? 'Nhận dạng sự kiện bất thường' : 'Phát hiện khuôn mặt' }}
              className='react-select'
              classNamePrefix='select'
              isDisabled={displaySelect}
              onChange={(e) => console.log(e.value)}
            />
          </Col>
        </Row>
        <Row>
        <Col md='8' className='mb-1'>
            <Select
              isMulti={false}
              isClearable={false}
              theme={selectThemeColors}
              placeholder={'Chọn bộ dữ liệu'}
              id={`language`}
              options={ChangeLisData(dataDataset)}
              className='react-select'
              classNamePrefix='select'
              isDisabled={displaySelect}
            //   value={setData(dataDataset)}
              onChange={(e) => console.log(e.value)}
            />
          </Col>
        </Row>
        <Row>
        <Col className='p-0' xl='4' style={{ marginLeft: '15px' }}>
            <h6 className='mb-2'>Thông tin bộ dữ liệu: {infoModel.datasetname}</h6>
            <CardText className='mb-25'>Loại: {infoModel.datasettype}</CardText>
            <CardText className='mb-25'>Loại sự kiện: {infoModel.datasettype}</CardText>
            <CardText className='mb-25'>Tổng số lượng mẫu: {infoModel.datasetsum}</CardText>
            <CardText className='mb-25' style={{ color: 'blue', cursor: 'pointer' }}> </CardText>
          </Col>
        </Row>
        <div className='d-flex justify-content-end'>
          <Button type='submit' color='primary' className='btn-next'>
            <span className='align-middle d-sm-inline-block d-none'>Tiếp theo</span>
            <ArrowRight size={14} className='align-middle ms-sm-25 ms-0'></ArrowRight>
          </Button>
        </div>
      </Form>
      
    </Fragment>
  )
}

export default ExpReport
