import axios from 'axios'
function getAuthToken() {
  return window.localStorage.getItem("access_token") ?? ""
}
import Avatar from '@components/avatar'
import { toast } from 'react-hot-toast'
import { X, Check } from 'react-feather'
// ** Get all Data
const getListUser = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    if (data.search) {
      await axios.get(`${url}/accounts/?pageSize=${data.pageSize}&page=${data.pageNumber}&search=${data.search}`, {
        headers: {
          'content-type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`
        }
      }).then(response => {
        dispatch({
          type: 'GET_USER',
          data: response.data
        })
      })
    } else {
    await axios.get(`${url}/accounts/?pageSize=${data.pageSize}&page=${data.pageNumber}`, {
      headers: {
        'content-type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`
      }
    }).then(response => {
      dispatch({
        type: 'GET_USER',
        data: response.data
      })
    })
    }
    }
   
}
const getListUserNoClass = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.get(`${url}/ds-hv-chuacolop/?page=1&pageSize=1000`, {
      headers: {
        'content-type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`
      }
    }).then(response => {
      dispatch({
        type: 'GET_USER_NOCLASS',
        data: response.data
      })
    })
  }
}
const searchUser = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.get(`${url}/accounts/?pageSize=${data.pageSize}&page=${data.pageNumber}&search=${data.search}`, {
      headers: {
        'content-type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`
      }
    }).then(response => {
      dispatch({
        type: 'SEARCH_USER',
        data: response.data
      })
    })
  }
}
const getInfo = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.get(`${url}/accounts/${data}/`, { withCredentials: true }).then(response => {
      dispatch({
        type: 'GET_INFO',
        data: response.data
      })
    })
  }
}
// update identification
const updatePass = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.put(`${url}/change-password/`, {
      new_password: data.new_password,
      old_password: data.old_password
    }, {
      withCredentials: true

    }).then(response => {
      dispatch({
        type: 'UPDATE_PASS',
        data: response.data
      })
      //   console.log(response.data.data[0].data)
    })
  }
}
const updateUser = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.put(`${url}/accounts/${data.id}/`, {
      email: data.email,
      name: data.name,
      usrfullname: data.usrfullname,
      usrdob: data.usrdob,
      roleid: data.roleid,
      usrfaculty: data.usrfaculty,
    }, {
      headers: {
        'content-type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`
      }

    }).then(response => {
      dispatch(getInfo({
        data: data.id
      }))
      toast(
        <div className='d-flex'>
          <div className='me-1'>
            <Avatar size='sm' color='success' icon={<Check size={12} />} />
          </div>
          <div className='d-flex flex-column'>
            <h6>Bạn đã cập nhật thông tin thành công!</h6>
          </div>
        </div>
      )
    }).catch(err => {
      toast(
        <div className='d-flex'>
          <div className='me-1'>
            <Avatar size='sm' color='danger' icon={<X size={12} />} />
          </div>
          <div className='d-flex flex-column'>
            <h6>Có lỗi xảy ra!</h6>
          </div>
        </div>
      )
    }
    )
  }
}
const deleteUser = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.delete(`${url}/accounts/${data}/`, {
      headers: {
        'content-type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`
      }

    }).then(response => {
      dispatch(getListUser({
        pageNumber: 1,
        pageSize: 10
      }))
      toast(
        <div className='d-flex'>
          <div className='me-1'>
            <Avatar size='sm' color='success' icon={<Check size={12} />} />
          </div>
          <div className='d-flex flex-column'>
            <h6>Bạn đã Xóa tài khoản thành công!</h6>
          </div>
        </div>
      )
    }).catch(err => {
      toast(
        <div className='d-flex'>
          <div className='me-1'>
            <Avatar size='sm' color='danger' icon={<X size={12} />} />
          </div>
          <div className='d-flex flex-column'>
            <h6>Có lỗi xảy ra!</h6>
          </div>
        </div>
      )
    }
    )
  }
}
const addUser = (data) => {
  const url = process.env.REACT_APP_API_URL
  return async dispatch => {
    await axios.post(`${url}/accounts/`, data, {
      headers: {
        'content-type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`
      }

    }).then(response => {
      dispatch(getListUser({
        pageNumber: 1,
        pageSize: 10
      }))
      toast(
        <div className='d-flex'>
          <div className='me-1'>
            <Avatar size='sm' color='success' icon={<Check size={12} />} />
          </div>
          <div className='d-flex flex-column'>
            <h6>Bạn đã thêm thông tin thành công!</h6>
          </div>
        </div>
      )
    }).catch(err => {
      toast(
        <div className='d-flex'>
          <div className='me-1'>
            <Avatar size='sm' color='danger' icon={<X size={12} />} />
          </div>
          <div className='d-flex flex-column'>
            <h6>Có lỗi xảy ra!</h6>
          </div>
        </div>
      )
    }
    )
  }
}
export {
  getListUser,
  updateUser,
  deleteUser,
  addUser,
  getInfo,
  updatePass,
  searchUser,
  getListUserNoClass
}
